import React, { Component } from 'react'

export default class AddPerson extends Component {

    state={
        name: '',
        age: '',
        city: '',
        hoobies: '',
        gender: ''
    }


    validName = (event) => {
        // console.log("work")
        // console.log(event.target.value);
        
        // The input field value
        let name = event.target.value;

        if(name.length >= 4){
            console.log("Good")
        } else {
            console.log("Bad")
        }
    }

    render() {
        return (
            <div>
                <input onChange={this.validName} placeholder="Name"/>
                <button>Add</button>
            </div>
        )
    }
}
