import React, {useState} from 'react'
import '../App.css'


function App() {

  const [num, setNum] = useState(0)
  const [flag, setFlag] = useState(true)

  const [onAirStyle, setOnAirStyle] = useState({
    borderRadius: "70%",
    height: "50px",
    width: "50px",
  })

  let bgColor = () => {
    setFlag(!flag)
    if(flag){
      setOnAirStyle({backgroundColor: "tomato", ...onAirStyle})
    }
    else {
      setOnAirStyle({backgroundColor: "white", ...onAirStyle})
    }
  }

  return (
    <div>
      <button onClick={()=>{setNum(num+1)}}>+</button>
      {num}
      <button onClick={()=>{setNum(num-1)}}>-</button>
      <br /><br />
      <button 
        style={onAirStyle}
        onClick={bgColor}
        >
          ON AIR
        </button>
    </div>
  );
}

export default App;
