import React, {useState} from 'react'
import '../App.css'


function App() {

  const [num, setNum] = useState(0)
  const [flag, setFlag] = useState(true)



  return (
    <div>
      <button onClick={()=>{setNum(num+1)}}>+</button>
      {num}
      <button onClick={()=>{setNum(num-1)}}>-</button>
      <br /><br />
      <button onClick={()=>{setFlag(!flag)}}>Change Flag</button>
    </div>
  );
}

export default App;
