import React, {useState} from 'react'

export default function Person(props) {
    
    const [id, setId] = useState(props.index)
    
    return (
        <div>
            <h2>{props.name}</h2>
            <p>Age: {props.age}</p>
            <button onClick={()=>{props.delete(id)}}>X</button>
        </div>
    )
}
