import React, {useState} from 'react'
import Person from './Person'
import AddPerson from './AddPerson'
import Navbar from './Navbar'
import Home from './Home'
import Contact from './Contact'
import About from './About'
import '../App.css'
import {BrowserRouter as Router, Switch, Route} from 'react-router-dom'


function App() {

  const [num, setNum] = useState(0)
  const [flag, setFlag] = useState(true)

  const [onAirStyle, setOnAirStyle] = useState({
    backgroundColor: "white",
    borderRadius: "70%",
    height: "50px",
    width: "50px"
  })

  const [list, setList] = useState([
    {name: "Pini", age: 22},
    {name: "Bill", age: 32},
    {name: "Tony", age: 27}
  ])

  const addNewPerson = (n, a) => setList([{name: n, age: a}, ...list])
  const deletePerson = (i) => setList(list.filter((item, index)=>(index!=i)))


  let bgColor = () => {
    setFlag(!flag)
    if(flag){
      setOnAirStyle({
        backgroundColor: "tomato",
        borderRadius: onAirStyle.borderRadius,
        height: onAirStyle.height,
        width: onAirStyle.width
      })
    }
    else {
      setOnAirStyle({
        backgroundColor: "white",
        borderRadius: onAirStyle.borderRadius,
        height: onAirStyle.height,
        width: onAirStyle.width
      })
    }
  }

  return (
    <div>

    <h1>Hello</h1>
      <Router>
        <Navbar />
        <Switch>
          <Route path='/' component={()=>{return <Home />}}/>
          <Route path='/about' component={()=>{return <About />}}/>
          <Route path='/contact' component={()=>{return <Contact />}}/>
        </Switch>
      </Router>

      <button onClick={()=>{setNum(num+1)}}>+</button>
      {num}
      <button onClick={()=>{setNum(num-1)}}>-</button>
      <br /><br />
      <button 
        style={onAirStyle}
        onClick={bgColor}
      >ON AIR</button>

      <AddPerson add={addNewPerson} />
      {list.map((item, i)=>{
        return (
          <Person 
            name={item.name} 
            age={item.age} 
            index={i}
            delete={deletePerson}
          />)
      })}

    </div>
  );
}

export default App;
