import React, {useState} from 'react'
import Person from './Person'
import AddPerson from './AddPerson'
import '../App.css'


function App() {

  const [num, setNum] = useState(0)
  const [flag, setFlag] = useState(true)
  const [onAirStyle, setOnAirStyle] = useState("white")

  const [list, setList] = useState([
    {name: "Pini", age: 22},
    {name: "Bill", age: 32},
    {name: "Tony", age: 27}
  ])

  const addNewPerson = (n, a) => setList([{name: n, age: a}, ...list])
  const deletePerson = (i) => setList(list.filter((item, index)=>(index!=i)))


  let bgColor = () => {
    setFlag(!flag)
    if(flag){
      setOnAirStyle("tomato")
    }
    else {
      setOnAirStyle("white")
    }
  }

  return (
    <div>
      <button onClick={()=>{setNum(num+1)}}>+</button>
      {num}
      <button onClick={()=>{setNum(num-1)}}>-</button>
      <br /><br />
      <button 
        className="onAir"
        style={{backgroundColor: onAirStyle}}
        onClick={bgColor}
      >ON AIR</button>

      <AddPerson add={addNewPerson} />
      {list.map((item, i)=>{
        return (
          <Person 
            name={item.name} 
            age={item.age} 
            index={i}
            delete={deletePerson}
          />)
      })}

    </div>
  );
}

export default App;
