import React, {useState} from 'react'
import Person from './Person'
import AddPerson from './AddPerson'
import '../App.css'

export default function App() {

    // my first hook - import useState first
    const [num, setNum] = useState(0)
    
    // On air state button style change
    const [flag, setFlag] = useState(true)
    const [onAirStyle, setOnAirStyle] = useState({
        backgroundColor: "white",
        borderRadius: '70%',
        height: "70px",
        width: "70px"
    })

    let bgChange = () => { 
        // num > 2 -> true/false
        // flag? true/false
        if(flag){
            setOnAirStyle({
                backgroundColor: "tomato",
                borderRadius: onAirStyle.borderRadius,
                height: onAirStyle.height,
                width: onAirStyle.width
            })
        }
        else {
            setOnAirStyle({
                backgroundColor: "white",
                borderRadius: onAirStyle.borderRadius,
                height: onAirStyle.height,
                width: onAirStyle.width
            })
        }
        setFlag(!flag)
    }

    // list of items, and add/delete a new person 
    const [list, setList] = useState([
        {name: "Dani", age: 12},
        {name: "Bill", age: 25},
        {name: "Victor", age: 64}
    ])


    // create new add person func to insert new object to list state
    // pass the func with props to the component AddPerson 
    const addNewPerson = (n, a) => setList([{name: n, age: a}, ...list])
    
    // delete a person from list by index

    //before: list = [A, B, C, D]
    const deletePerson = (i) => setList(list.filter((item, index)=>(index!=i)))
    //after: (2) => setList([A, B, D]) 

    // 0 - A  x //(2) => setList(filter(0!=2)) true - in 
    // 1 - B  x //(2) => setList(filter(1!=2)) true - in 
    // 2 - C  x //(2) => setList(filter(2!=2)) false - out
    // 3 - D  x //(2) => setList(filter(3!=2)) true - in 


    return (
        <div>
            <h1>Hooks and Routers</h1>
            <button onClick={()=>{setNum(num+1)}}>+</button>
            {num}
            <button onClick={()=>{setNum(num-1)}}>-</button>
            <br /><br />
            <button style={onAirStyle} onClick={bgChange}>ON AIR</button>
            <br /><br />

            <AddPerson add={addNewPerson}/>

            {list.map((item, index)=>{
                return (
                    <Person
                        name={item.name}
                        age={item.age}
                        delete={deletePerson}
                        i={index}
                    />
                )
            })}

        </div>
    )
}
