import React, {useState} from 'react'
import '../App.css'

export default function App() {

    // my first hook - import useState first
    const [num, setNum] = useState(0)
    
    // On air state button style change
    const [flag, setFlag] = useState(true)
    const [onAirStyle, setOnAirStyle] = useState({
        backgroundColor: "white",
        borderRadius: '70%',
        height: "70px",
        width: "70px"
    })

    const [list, setList] = useState([
        {name: "Dani", age: 12},
        {name: "Bill", age: 25},
        {name: "Victor", age: 64}
    ])


    let bgChange = () => { 
        // num > 2 -> true/false
        // flag? true/false
        if(flag){
            setOnAirStyle({
                backgroundColor: "tomato",
                borderRadius: onAirStyle.borderRadius,
                height: onAirStyle.height,
                width: onAirStyle.width
            })
        }
        else {
            setOnAirStyle({
                backgroundColor: "white",
                borderRadius: onAirStyle.borderRadius,
                height: onAirStyle.height,
                width: onAirStyle.width
            })
        }
        setFlag(!flag)
    }

    return (
        <div>
            <h1>Hooks and Routers</h1>
            <button onClick={()=>{setNum(num+1)}}>+</button>
            {num}
            <button onClick={()=>{setNum(num-1)}}>-</button>
            <br /><br />
            <button style={onAirStyle} onClick={bgChange}>ON AIR</button>
            <br /><br />
        </div>
    )
}
