import React, {useState} from 'react'

export default function Product(props) {

    const [bgColor, setBgColor] = useState('tomato')
    const [condition, setCondition] = useState(props.condition)

    let conditionStyle = () => {
        if(condition) {
            setBgColor('tomato')
        }
        else {
            setBgColor('limegreen')
        }
    }

    return (
        <div onClick={props.changeColor()} className="product" style={{backgroundColor: bgColor}}>
            <h4>{props.productName}</h4>
        </div>
    )
}
