import React, {useState} from 'react'
import {Link} from 'react-router-dom'
import Product from './Product'
import AddProduct from './AddProduct'

export default function Room(props) {

    const [show, setShow] = useState(false)

    const displaySectionAdd = ()=>{if(show) return <AddProduct add={props.addProduct} roomIndex={props.index}/>}

    return (
        <div>
            <h2>Name: {props.name}</h2>
            <h2>Room type: {props.type}</h2>
            {props.products.map(p=>{ return <Product /> })}
            <button onClick={()=>{setShow(!show)}}>ADD NEW PRODUCT</button>
            <Link to='/'><button>BACK</button></Link> 
            {displaySectionAdd()}
        </div>
    )
}
