import React, {useState} from 'react'
import {Link} from 'react-router-dom'
import Product from './Product'
import AddProduct from './AddProduct'

export default function Room(props) {

    const [show, setShow] = useState(false)

    const displaySectionAdd = ()=>{
        if(show) 
        return <AddProduct show={setShow} addProduct={props.add} roomIndex={props.index}/>
        else{
            return null
        }
    }

    return (
        <div>
            <h2>Name: {props.name}</h2>
            <h2>Room type: {props.type}</h2><br />
            {props.products.map(p=>{ return <Product productName={p.productName} condition={p.isTurn}/> })}
            <button onClick={()=>{setShow(!show)}}>ADD NEW PRODUCT</button>
            <Link to='/'><button>BACK</button></Link> 
            {displaySectionAdd()}
        </div>
    )
}
