import React, {useState} from 'react';
import AddRoom from './AddRoom'
import Rooms from './Rooms'
import '../App.css';

function App() {

  const [rooms, setRooms] = useState([])
  // const [show, setShow] = useState(false)

  const addNewRoom = (n, c, t) => {
    setRooms([...rooms, {name: n, type: t, color: c, products: []}])
  }

  const checkRooms = () => {
    if(rooms.length > 0){
      return <Rooms rooms={rooms}/>
    }
  }

  return (
    <div className="App">
      <h1>SmartHome</h1>
      {checkRooms()}
      {/* <button onClick={()=>{setShow(!show)}}>ADD NEW ROOM</button> */}
      <AddRoom add={addNewRoom} />

    </div>
  );
}

export default App;
