import React, {useState} from 'react'

export default function Product(props) {

    const [bgColor, setBgColor] = useState('')

    let conditionStyle = () => {
        if(props.condtion == "off") {
            setBgColor('tomato')
        }
        else {
            setBgColor('limegreen')
        }
    }

    return (
        <div style={{backgroundColor: bgColor}}>
            <h4>{props.productName}</h4>
        </div>
    )
}
