import React, {useState} from 'react';
import {BrowserRouter as Router, Switch, Route, Link} from 'react-router-dom'
import AddRoom from './AddRoom'
import Rooms from './Rooms'
import Room from './Room'
import '../App.css';

function App() {

  const [rooms, setRooms] = useState([])

  const addNewRoom = (n, c, t) => {
    setRooms([...rooms, {name: n, type: t, color: c, products: []}])
  }

  const addNewProduct = (p, i) => {

    const product = {productName: p, isTurn: "off"}
    rooms[i].products.push(product) 
    setRooms(rooms)
  }

  return (
    <div className="App">
      <h1>SmartHome</h1>
      <Router>
        <Switch>
          <Route exact path='/' component={()=>{return <Rooms rooms={rooms}/>}}/>
          <Route path='/addroom' component={()=>{return <AddRoom add={addNewRoom}/>}}/>
          {rooms.map(r=>{
            return (
              <Route path={`/room${r.name}`} component={()=>{
                return (
                  <Room name={r.name} color={r.color} type={r.type} products={r.products}/> 
                )}}
              />
            )
          })}
        </Switch>
      </Router>
    </div>
  );
}

export default App;
