import React, {useState} from 'react'
import { Consumer } from './ContextAPI'

export default function Product(props) {

    const [bgColor, setBgColor] = useState('tomato')
    const [roomIndex, setRoomIndex] = useState(props.roomIndex)
    const [productIndex, setProductIndex] = useState(props.productIndex)

    const checkConditoin = () => {
        if(props.condition) {
            setBgColor("green")
        } else {
            setBgColor("tomato")
        }
     }

    return (
        <Consumer>
            {(rooms)=>{
                <button 
                    onClick={()=>{
                        props.changeColor(roomIndex, productIndex);
                        checkConditoin()
                    }} 
                    className="product" 
                    style={{backgroundColor: bgColor}}
                >
                    {props.productName}
                </button>
            }}
        </Consumer>
    )
}
