import React, {useState} from 'react'

export default function Product(props) {

    const [bgColor, setBgColor] = useState('tomato')

    const checkConditoin = () => {
        if(props.condition) {
            setBgColor("green")
        } else {
            setBgColor("tomato")
        }
     }

    return (
        <button onClick={()=>{props.changeColor(props.roomIndex, props.productIndex); checkConditoin()}} className="product" style={{backgroundColor: bgColor}}>
            {props.productName}
        </button>
    )
}
