export default function Product(props) {

    const [bgColor, setBgColor] = useState('tomato')
    const [condition, setCondition] = useState(props.condition)

    return (
        <button onClick={()=>{props.changeColor(props.roomIndex, props.productIndex)}} className="product" style={{backgroundColor: bgColor}}>
            <h4>{props.productName}</h4>
        </button>
    )
}
