import React, {useState} from 'react'

export default function Product(props) {

    const [bgColor, setBgColor] = useState('')

    const checkBgColor = () => {
        if(props.condition) {
            setBgColor("limegreen")
        }else{
            setBgColor('tomato')
        }
        return bgColor;
    }

    return (
        <div>
            {checkBgColor()}
            <div 
                className="product" 
                style={{backgroundColor: bgColor}}
                onClick={()=>{props.changeColor(props.roomIndex, props.productIndex)}} 
            >
                <h4>{props.productName}</h4>
            </div>
        </div>
    )
}
